import React from "react";
export default function Itinerary({date,itinerary}){

    let D=new Date(date)
    const weekday = [
        "Sunday",
        "Monday",
        "Tuesday",
        "Wednesday",
        "Thursday",
        "Friday",
        "Saturday",
      ];
      const Month=[
        "January",
        "February",
        "March",
        "April",
        "May",
        "June",
        "July",
        "August",
        "September",
        "October",
        "November",
        "December"
      ]
    return(
        <div class="grid grid-cols-3">
            <div class="col-span-2 px-20 py-10">

            <h1 className=" text-7xl fonthead ">Itinerary</h1>

            <h2 className="my-10 text-2xl border-b-[2px] inline-block py-2 border-pi" >{weekday[D.getDay()]},{Month[D.getMonth()]}{D.getDate()}</h2>

             {
                itinerary.map((item,index)=>{
                    return(
                        <div>
                         <h1> 
                         <h2 className="">
                         {item.time}
                         </h2>
                         <h2>{item.title}</h2>
                         <h2>{item.description}</h2>
                         </h1>
                        </div>
                    )
                })

             }

            </div>
            <img src="./images/save the date images/iternary img.png" class="w-full object-cover mr-0"/>

        </div>

    )
}
